mapper.update:
update dish
name = #{name}, category_id = #{categoryId}, price = #{price}, image = #{image}, description = #{description}, status = #{status}, create_time = #{createTime}, update_time = #{updateTime}, create_user = #{createUser}, update_user = #{updateUser}
where id = #{id}

mapper.select:
select d.*, c.name as categoryName from dish d left join category c on d.category_id = c.id
1=1 and d.name like concat('%',#{name},'%') and d.category_id = #{categoryId} and d.status = #{status}
order by update_time desc

select * from dish

    1=1
    and id = #{id}
    and name like concat('%',#{name},'%')
    and category_id = #{categoryId}
    and price = #{price}
    and description like concat('%',#{description},'%')
    and status = #{status}

select count(id) from dish


        and status = #{status}


        and category_id = #{categoryId}
